(window.webpackJsonp=window.webpackJsonp||[]).push([[18],{346:function(e,t,r){"use strict";r.r(t);var n=r(2),o=Object(n.a)({},(function(){var e=this,t=e.$createElement,r=e._self._c||t;return r("ContentSlotsDistributor",{attrs:{"slot-key":e.$parent.slotKey}},[r("p",[r("strong",[r("a",{attrs:{href:"http://www.inmediahk.net/node/1021064",target:"_blank",rel:"noopener noreferrer"}},[e._v("原文"),r("OutboundLink")],1),e._v("为繁体，因对“记忆宫殿”的记忆术比较感兴趣，故转为简体。")])]),e._v(" "),r("p",[r("img",{attrs:{src:"https://images-blogger-opensocial.googleusercontent.com/gadgets/proxy?url=http%3A%2F%2Fwww.inmediahk.net%2Ffiles%2Fcolumn_images%2Fp005.jpg%3F1393090856&container=blogger",alt:""}}),e._v("\n图片"),r("a",{attrs:{href:"http://www.inmediahk.net/files/imagecache/w456/column_images/p005.jpg",target:"_blank",rel:"noopener noreferrer"}},[e._v("原地址"),r("OutboundLink")],1),e._v("，需科学上网")]),e._v(" "),r("p",[e._v("明朝有一鬼佬叫利玛窦，远从意大利来到中国，为的是传教。用意大利语在华传教，当然行不通。因此，利玛窦来到中国，第一要务乃是学中文。")]),e._v(" "),r("p",[e._v("他，当然不是第一个要学中文的意大利人，就是近在眼前，也有一位罗明坚神父──与利玛窦一样是来传教的──他们在澳门招聘中文老师，后来又开中文布道会，练习传教。结果，这两个人获准在肇庆建立国内第一座天主教堂仙花寺，开启天主教在华历史。")]),e._v(" "),r("p",[e._v("后来，罗明坚因事回国，利玛窦独自上路，周游中国各地，最后走到北京城中，长眠皇城脚下。他在文人界极受欢迎，除了是因为文人见一鬼佬会讲中文感觉新奇，更是因为他们发现：这个人将儒学经典倒背如流，更胜吾人之上！他一定有什么好方法，才可以背得那么流畅。")]),e._v(" "),r("p",[e._v("那时候，利玛窦人在南京，弟子瞿太素为他张罗各事，向文人介绍这位「泰西儒士」（"),r("em",[e._v("注：其原名中文直译为马泰奥·里奇，利玛窦是他的漢名，号西泰，又号清泰、西江。在中国颇受士大夫的敬重，尊称为“泰西儒士”。来自"),r("a",{attrs:{href:"http://zh.wikipedia.org/wiki/%E5%88%A9%E7%8E%9B%E7%AA%A6",target:"_blank",rel:"noopener noreferrer"}},[e._v("维基百科"),r("OutboundLink")],1)]),e._v("）。日子一久，利玛窦便奇怪：为什么中国人来见我，老是问记忆法门？不过也好，既然他们问记忆术，那就教吧。我扬名立万，才有机会向他们介绍真道。利玛窦便教导说：我是这样做的，首先在脑海中想像一个大宫殿，宫里有很多房间；每一个房间都放置一类事物。只要想起一类事物中的一个，就能记得全部了。")]),e._v(" "),r("p",[e._v("文人听了，心想这还不简单，回家就要子弟学利玛窦的记忆法。可是，过了大半年，子弟记忆力也不见进步。文人见了，认为利玛窦有意留了一手，不肯倾囊相授，不过这也是利子（"),r("em",[e._v("注：利子指利玛窦，“他在中国颇受士大夫的敬重，尊称其为“泰西”先生、“利先生”、“利子”、“利进士”等。”来源"),r("a",{attrs:{href:"http://vip.book.sina.com.cn/chapter/5207733/10162347.html",target:"_blank",rel:"noopener noreferrer"}},[e._v("这里"),r("OutboundLink")],1)]),e._v("）绝活，不授也不奇怪，后来也没再去问利玛窦。利玛窦也知道成效不彰，不过文人没再问，他也就算了，事情便不了了之。只是利玛窦也奇怪：我明明是这样学中文的啊，为什么他们不行呢？")]),e._v(" "),r("p",[e._v("答案很简单。各位就请随便打开一本港台出版的中文字典。封面内页，214个部首，便是利玛窦口中所说那座「记忆宫殿」。每一个部首，便是一个房间。每一个房间都放置一类事物。虽然不一定能做到想起一个便记得全部，但要从正确的房间，取用正确的字词，易如反掌。为什么他们不行呢？因为他们──也就是我们这三千万个还在用正体中文字的人──自从四五岁学识字开始，就在记忆宫殿里捉迷藏了。")]),e._v(" "),r("p",[e._v("这套记忆法，利玛窦还以为是自己独创的，不过学中文学到能独创一套记忆法，也算是前无古人了。至少学中文学了几千年的中国人，自己就没注意到中文字的规律本身就藏着一套记忆法，甚至还要自己将之破坏掉。「规范汉字」无规无矩，从不成方圆，如今却蔚成主流，谁人之功，显然易见。")]),e._v(" "),r("p",[e._v("是的，毋可否认，「规范汉字」很省墨水。不过，却连中国潜在的软实力也省掉了。除了靠发音死记，外国人还可以怎样学中文呢？中文学不好，又怎能心慕华化，恨不得来生当个中国人呢？且作预言：从此以后，不会再有一个利玛窦为了学中文，想出一套记忆法：不是因为有人发现在先，而是因为这规律，早就烟消云散。是谁杀的？自杀的。")]),e._v(" "),r("p",[e._v("（图片：随手在Google上找「部首表」，就会有一大片不同排版的部首表，但泰半都是简体字的所谓部首。大陆用的中文字典，实更鼓励学生用拼音查字。）")])])}),[],!1,null,null,null);t.default=o.exports}}]);